('GRIDWIX', 2153, 645, 10, '', '\n	$(\"#FILE_{G.GRPID}-{F.FNCID}\").on(\"change\", function(e){\n		alog(\"FILE_{G.GRPID}-{F.FNCID}.change().................start\");\n		var files = e.target.files; //input file 객체를 가져온다.\n		var i,f;\n		for (i = 0; i != files.length; ++i) {\n			f = files[i];\n			var reader = new FileReader(); //FileReader를 생성한다.         \n\n			//성공적으로 읽기 동작이 완료된 경우 실행되는 이벤트 핸들러를 설정한다.\n			reader.onload = function(e) {\n\n			   var data = e.target.result; //FileReader 결과 데이터(컨텐츠)를 가져온다.\n\n			   //바이너리 형태로 엑셀파일을 읽는다.\n			   var workbook = XLSX.read(data, {type: \'binary\'});\n\n			   //엑셀파일의 시트 정보를 읽어서 JSON 형태로 변환한다.\n			   workbook.SheetNames.forEach(function(item, index, array) {\n				   EXCEL_JSON = XLSX.utils.sheet_to_json(workbook.Sheets[item]);\n				   alog(EXCEL_JSON);\n				   $$(\"wixdt{G.GRPID}\").parse(EXCEL_JSON, \"json\");\n\n					$(\"#span{G.GRPID}Cnt\").text($$(\"wixdt{G.GRPID}\").count());\n\n				});//end. forEach\n\n			}; //end onload\n\n			//파일객체를 읽는다. 완료되면 원시 이진 데이터가 문자열로 포함됨.\n			reader.readAsBinaryString(f);\n\n		}//end. for\n	\n	});\n', '', '', '', 'R', 'F.FNCCD=EXCELIMPORT', '', '20200628205851', '20200628214446'),
