(3, 'GRID', 4, 'HTMLJS', 'SEARCH', 'SVRSVC', 1, '그리드조회', '', '    //그리드 조회({G.GRPNM})	\n    function {G.GRPID}_SEARCH(tinput,token){\n        alog(\"{G.GRPID}_SEARCH()------------start\");\n\n		var tGrid = mygrid{G.GRPID};\n\n        //그리드 초기화\n        tGrid.clearAll();\n\n        //불러오기\n        $.ajax({\n            type : \"POST\",\n            url : url_{G.GRPID}_{G.FNCID}+\"&TOKEN=\" + token + \" &{G.GRPID}_CRUD_MODE=read&\" + tinput ,\n            data : tinput,\n            dataType: \"json\",\n            async: true,\n            success: function(data){\n                alog(\"   gridSearch6 json return----------------------\");\n                alog(\"   json data : \" + data);\n                alog(\"   json RTN_CD : \" + data.RTN_CD);\n                alog(\"   json ERR_CD : \" + data.ERR_CD);\n                //alog(\"   json RTN_MSG length : \" + data.RTN_MSG.length);\n\n                //그리드에 데이터 반영\n                if(data.RTN_CD == \"200\"){\n					var row_cnt = 0;\n					if(data.RTN_DATA){\n						row_cnt = data.RTN_DATA.rows.length;\n						$(\"#span{G.GRPID}Cnt\").text(row_cnt);\n\n						tGrid.parse(data.RTN_DATA,\"json\");\n						\n					}\n					msgNotice(\"[{G.GRPNM}] 조회 성공했습니다. (\"+row_cnt+\"건)\",1);\n\n                }else{\n                    msgError(\"[{G.GRPNM}] 서버 조회중 에러가 발생했습니다.RTN_CD : \" + data.RTN_CD + \"ERR_CD : \" + data.ERR_CD + \"RTN_MSG :\" + data.RTN_MSG,3);\n                }\n            },\n            error: function(error){\n				msgError(\"[{G.GRPNM}] Ajax http 500 error ( \" + error + \" )\",3);\n                alog(\"[{G.GRPNM}] Ajax http 500 error ( \" + error + \" )\");\n            }\n        });\n\n        alog(\"gridSearch{G.GRPID}()------------end\");\n    }\n', '', '', '', 'R', '', '', 'N', '20140712125814', '20180427072240'),
