(125, 3, 65, 'sLgnFailG', 'sLgnFailG', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT USR_ID, count(LOGIN_SEQ) as LOGIN_CNT \nFROM CMN_LOG_LOGIN\nWHERE SUCCESS_YN = \'N\'\nGROUP by USR_ID ORDER BY count(login_seq) desc', '20180511142644', NULL),
(126, 3, 65, 'sLgnIpG', 'sLgnIpG', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT REMOTE_ADDR, count(LOGIN_SEQ) as LOGIN_CNT \nFROM CMN_LOG_LOGIN\nWHERE SUCCESS_YN = \'N\'\nGROUP by REMOTE_ADDR ORDER BY count(login_seq) desc', '20180511142716', NULL),
(127, 3, 65, 'sAuthNoG', 'sAuthNoG', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT USR_ID,count(LAUTH_SEQ) as AUTH_CNT\nFROM `CMN_LOG_AUTH` \nWHERE SUCCESS_YN = \'N\'\nGROUP BY USR_ID\nORDER BY count(LAUTH_SEQ) desc', '20180511142741', NULL),
(128, 3, 65, 'sLgnLockG', 'sLgnLockG', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT USR_ID, count(LOGIN_SEQ) as LOGIN_CNT \nFROM CMN_LOG_LOGIN\nWHERE LOCKCD in (\'GOLOCK\')\nGROUP by USR_ID ORDER BY count(login_seq) desc ', '20180511142804', NULL),
(129, 3, 65, 'sAuthPiG', 'sAuthPiG', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT b.USR_ID,count(a.LAUTHD_SEQ) as REQ_PI_CNT,sum(a.ROW_CNT) as VIEW_ROW_SUM \nFROM CMN_LOG_AUTHD a\n	JOIN CMN_LOG_AUTH b ON a.REQ_TOKEN = b.REQ_TOKEN\nWHERE PI_IN_COLIDS != \'\' AND PI_IN_COLIDS is not NULL\nGROUP BY b.USR_ID\nORDER BY count(a.ROW_CNT) desc', '20180511142828', NULL),
(130, 3, 65, 'iMonthG', 'iMonthG', 4, 'C', 'RTN_INT', 0, 'INSERT INTO CMN_LOG_CFM (\n	FROM_DT, TO_DT, CFM_DESC, ADD_DT, ADD_ID \n) VALUES (\n	replace(#{G2-FROM_DT},\'.\',\'\'), replace(#{G2-TO_DT},\'.\',\'\'), #{G2-CFM_DESC}, date_format(sysdate(),\'%Y%m%d%H%i%s\'), #{USER.SEQ}\n)', '20180511142906', '20180511150351'),
