(133, 3, 66, 'sLogin', 'LOGIN', 4, 'R', 'RTN_SELECT_LIST', 0, 'SELECT substring(add_dt,1,8) as LOGIN_DT\n	, count(login_seq) as LOGIN_CNT \n	, count(login_seq)+10 as LOGIN_CNT2\nFROM CMN_LOG_LOGIN\nGROUP BY substring(add_dt,1,8)\nORDER BY substring(add_dt,1,8)\n', '20180515205842', '20180524081848'),
(134, 3, 67, 'sPgmG', 'PGM', 3, 'R', 'RTN_SELECT_LIST', 0, 'SELECT \n        0 as CHK, PGMSEQ, PGMID, PGMNM, PKGGRP, VIEWURL, PGMTYPE, SECTYPE, ADDDT, MODDT\n    FROM \n        CG.CG_PGMINFO\n    WHERE PJTSEQ = #{G1-PJTSEQ}\n', '20180528073431', '20180528080545'),
(135, 3, 67, 'sAuthG', 'AUTH', 3, 'R', 'RTN_SELECT_LIST', 0, '  SELECT \n 		0 as CHK\n		, concat(p.PGMID,\'-\',g.GRPID,\'_\',f.FNCID) as ROWID\n      ,p.PGMID \n      ,concat(g.GRPID,\'_\',f.FNCID) as AUTH_ID\n      ,concat(g.GRPNM,\'_\',f.FNCNM) as AUTH_NM \n  FROM \n      CG.CG_PGMGRP g\n      JOIN CG.CG_PGMFNC f on g.GRPSEQ = f.GRPSEQ and g.PGMSEQ = f.PGMSEQ\n      JOIN CG.CG_PGMINFO p on p.PGMSEQ = g.PGMSEQ\n  WHERE p.PJTSEQ = #{G1-PJTSEQ} AND ( f.FNCTYPE != \'\' && f.FNCTYPE is not null )\n      order by p.PGMID,g.GRPORD asc,f.FNCORD asc  \n', '20180528073504', '20180528080545'),
(136, 3, 67, 'sFileG', 'FILE', 3, 'R', 'RTN_SELECT_LIST', 0, '    SELECT \n        0 as CHK, PGMSEQ, VERSEQ, FILESEQ, FILETYPE, FILENM, FILEHASH, FILESIZE, ADDDT, MODDT\n    FROM \n        CG.CG_RSTFILE\n    WHERE PJTSEQ = #{G1-PJTSEQ} and ACTIVEYN = \'Y\'\n', '20180528073504', '20180528081305');
