('GRID', 2, 356, 170, '블럭선택복사', '		//블럭선택 및 복사\n		mygrid{G.GRPID}.enableBlockSelection(true);\n		mygrid{G.GRPID}.attachEvent(\"onKeyPress\",function(code,ctrl,shift){\n			alog(\"onKeyPress.......code=\" + code + \", ctrl=\" + ctrl + \", shift=\" + shift);\n\n			//셀편집모드 아닐때만 블록처리\n			if(!mygrid{G.GRPID}.editor){\n				mygrid{G.GRPID}.setCSVDelimiter(\"	\");\n				if(code==67&&ctrl){\n					mygrid{G.GRPID}.copyBlockToClipboard();\n\n					var top_row_idx = mygrid{G.GRPID}.getSelectedBlock().LeftTopRow;\n					var bottom_row_idx = mygrid{G.GRPID}.getSelectedBlock().RightBottomRow;\n					var copyRowCnt = bottom_row_idx-top_row_idx+1;\n					msgNotice( copyRowCnt + \"개의 행이 클립보드에 복사되었습니다.\",2);\n\n				}\n				if(code==86&&ctrl){\n					mygrid{G.GRPID}.pasteBlockFromClipboard();\n\n					//row상태 변경\n					var top_row_idx = mygrid{G.GRPID}.getSelectedBlock().LeftTopRow;\n					var bottom_row_idx = mygrid{G.GRPID}.getSelectedBlock().RightBottomRow;\n					for(j=top_row_idx;j<=bottom_row_idx;j++){\n						var rowID = mygrid{G.GRPID}.getRowId(j);\n						RowEditStatus = mygrid{G.GRPID}.getUserData(rowID,\"!nativeeditor_status\");\n						if(RowEditStatus == \"\"){\n							mygrid{G.GRPID}.setUserData(rowID,\"!nativeeditor_status\",\"updated\");\n							mygrid{G.GRPID}.setRowTextBold(rowID);\n						}\n					}\n				}\n				return true;\n			}else{\n				return false;\n			}\n		});\n', '', '', '', 'R', '', '', '20180103125737', '20180827200609'),
