('FORMVIEW', 251, 'HTMLJS', 'DELETE', 'SVRSVC', 10, '', '', '//FORMVIEW DELETE\nfunction {G.GRPID}_{F.FNCID}(){	\n	alog(\"{G.GRPID}_{F.FNCID}---------------start\");\n\n	//조회했는지 확인하기\n	if( $(\"#{G.GRPID}-CTLCUD\").val() != \"R\" ){\n		alert(\"조회된 것만 삭제 가능합니다.\");\n		return;\n	}\n	//확인\n	if(!confirm(\"정말로 삭제하시겠습니까?\")){\n		return;\n	}\n	\n	//삭제처리 명령어\n	$(\"#{G.GRPID}-CTLCUD\").val(\"D\");\n\n	//폼객체를 불러와서\n	var form1 = $(\"#formview{G.GRPID}\")[0];\n\n	//FormData parameter에 담아줌\n	var formData = new FormData(form1);\n\n	$.ajax({\n		type : \"POST\",\n		url : url_{G.GRPID}_{F.FNCID},\n		data : formData,\n		processData: false,\n		contentType: false,\n		success: function(tdata){\n			alog(tdata);\n			data = jQuery.parseJSON(tdata);\n			//alert(data);\n			if(data && data.RTN_CD == \"200\"){\n				msgNotice(\"정상적으로 삭제되었습니다.\",1);\n			}else{\n				msgError(\"오류가 발생했습니다(\"+ data.ERR_CD + \").\" + data.RTN_MSG,3);\n			}\n		},\n		error: function(error){\n			alog(\"Error:\");\n			alog(error);\n		}\n	});\n}\n', '', '', '', 'R', '', 'DHTMLX', '', '20171206122702', '20180309110425'),
